mapper.select:
SELECT count(1) FROM xxl_job_info t
AND t.job_group = #{jobGroup}
AND t.trigger_status = #{triggerStatus}
AND t.job_desc like CONCAT(CONCAT('%', #{jobDesc}), '%')
AND t.executor_handler like CONCAT(CONCAT('%', #{executorHandler}), '%')
AND t.author like CONCAT(CONCAT('%', #{author}), '%')

select  *  from (
SELECT row_number() over(ORDER BY id DESC) r,
FROM xxl_job_info  t


		AND t.job_group = #{jobGroup}


		AND t.trigger_status = #{triggerStatus}


		AND t.job_desc like '%'||#{jobDesc,jdbcType=VARCHAR}||'%'


		AND t.executor_handler like '%'||#{executorHandler,jdbcType=VARCHAR}||'%'


		AND t.author like '%'||#{author,jdbcType=VARCHAR}||'%'


) where r between #{offset}+1 and #{offset}+#{pagesize}

SELECT 
FROM xxl_job_info t
WHERE t.id = #{id}

SELECT 
FROM xxl_job_info t
WHERE t.job_group = #{jobGroup}

select ROWNUM ROW_ID, TEMP_PAGE.* from (
SELECT 
FROM xxl_job_info t
WHERE t.trigger_status = 1
and t.trigger_next_time  <=  #{maxNextTime}
ORDER BY id ASC
) TEMP_PAGE WHERE ROWNUM  <=  #{pagesize}

mapper.update:
UPDATE xxl_job_info
SET
	trigger_last_time = #{triggerLastTime},
	trigger_next_time = #{triggerNextTime},
	trigger_status = #{triggerStatus}
WHERE id = #{id}
